/** @jsx React.DOM */
var React = require('react');
var MapTitle = require('app/components/map-title');
var Map = require('app/components/map');

module.exports = React.createClass({
  render: function () {
    return  <div>
              <div
                id="title"
                className="map-title" >
                <MapTitle name={this.props.title} />
              </div>
              <div id="map-container" >
                <Map id="map" width={960} height={500} selector="#map" data={this.props.data} />
              </div>
            </div>;

  }

});